package org.tnsif.intro;

public class DataTypesDemo {
//Performing data type operations
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 127;
		//byte = 1 byte
		int  b = 128;
		//int = 4 byte
		short b1 = 135;
		long b2 = 654646464l;
		float c = 12/5;
		float d = 5/2.5f;
		double e = 12/5;
		double f = 12d/5d;
		
		
		//character
		char g = 'M';
		//Ascci Value
		int h = 'A';
		
		String str = "Manoj";
		//Boolean 
		boolean i = true;
		
		System.out.println("The Value byte of a is: " +a);
		System.out.println("The Value int  of b is: " +b);
		System.out.println("The Value short of b1 is: " +b1);
		System.out.println("The Value long of b2 is: " +b2);
		System.out.println("The Value float of c is: " +c);
		System.out.println("The Value float of d is: " +d);
		System.out.println("The Value double of e is: " +e);
		System.out.println("The Value double of f is: " +f);
		System.out.println("The Value char of g is: " +g);
		System.out.println("ascci value of A is: " +h);
		System.out.println("The Value of str is: " +str);
		System.out.println("The Value of boolean i is: " +i);
		
		
		
		
		
	}

}
